mapper.select:
SELECT IFNULL(MAX(number), 0) as max_Number
FROM interviewworks.w001
WHERE accountNumber = #{accountNumber}
  AND dataNumber = #{dataNumber}
  AND type = #{type}

select *
from interviewWorks.w001
where accountNumber like #{accountNumber}
  and type like #{type}
  and money >= #{money}
order by number asc

mapper.update:
update interviewworks.w001
set money     = #{money},
    update_cd = '修改'
where accountNumber = #{accountNumber}
  and dataNumber = #{dataNumber}
  and number = #{number}
  and type = #{type}

mapper.delete:
delete
from interviewworks.w001
where accountNumber = #{accountNumber}
  and dataNumber = #{dataNumber}
  and number = #{number}
  and type = #{type}
